mapper.insert:

  SELECT LAST_INSERT_ID()

insert into pq_sensitive_word (word, updated_time, created_time
  )
values (#{word,jdbcType=VARCHAR}, #{updatedTime,jdbcType=TIMESTAMP}, #{createdTime,jdbcType=TIMESTAMP}
  )

mapper.update:
update pq_sensitive_word
set word = #{word,jdbcType=VARCHAR},
  updated_time = #{updatedTime,jdbcType=TIMESTAMP},
  created_time = #{createdTime,jdbcType=TIMESTAMP}
where id = #{id,jdbcType=BIGINT}

mapper.select:
select id, word, updated_time, created_time
from pq_sensitive_word
where id = #{id,jdbcType=BIGINT}

select id, word, updated_time, created_time
from pq_sensitive_word
where word = #{word,jdbcType=VARCHAR}
